        param1 = _param1;
        param2 = _param2;
        __UUPSUpgradeable_init();
        owner = msg.sender;
    }

    // Function to update parameters (for demonstration)
    function updateParams(uint _newParam1, string memory _newParam2) public {
        param1 = _newParam1;
        param2 = _newParam2;
    }

